import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  id: 98,
  code: "INR",
  name: "Rupee",
  symbol: "â‚¹",
  exchangeRate: 83,
  country: "India",
  dial_code: "+91",
  flag: "",
};

const currencySlice = createSlice({
  name: "CurrencySlice",
  initialState,
  reducers: {
    initialCurrencyData: (state, { payload }) => {
      const { id, code, name, symbol, exchangeRate, country, flag, dial_code } =
        payload;

      state.id = id;
      state.code = code;
      state.name = name;
      state.symbol = symbol;
      state.exchangeRate = Math.trunc(exchangeRate);
      state.country = country;
      state.dial_code = dial_code;
      state.flag = flag;
      return state;
    },
  },
});

export const { initialCurrencyData } = currencySlice.actions;

export const currencyReducer = currencySlice.reducer;

export const currencyState = (state) => state.currency;
